// Process the incoming data from frontend
const data = $input.first().json;
console.log('Received webhook data:', data);

// Extract data for Airtable and Instagram
const videoData = data.videoData || {};
const title = data.title || 'Viral Video Title';
const accountId = data.accountId || 'unknown';
const accountName = data.accountName || 'Unknown Account';

// Use Cloudinary URL if available, otherwise fallback
// Check both cloudinaryUrl and airtableVideoUrl for the real URL
const videoUrl = videoData.cloudinaryUrl || videoData.airtableVideoUrl || 'https://res.cloudinary.com/dtkps2uzi/video/upload/v1756999096/samples/elephants.mp4';

console.log('Selected video URL:', videoUrl);
console.log('Video data keys:', Object.keys(videoData));
console.log('Cloudinary URL:', videoData.cloudinaryUrl);
console.log('Airtable URL:', videoData.airtableVideoUrl);

return {
  success: true,
  workflowId: Date.now().toString(),
  processedAt: new Date().toISOString(),
  videoData: videoData,
  title: title,
  accountId: accountId,
  accountName: accountName,
  videoUrl: videoUrl,
  message: 'Video processed successfully for Instagram posting'
};
